//随机数，包括min和max
function randownInt(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

//获取当前页面名字，比如company.html
function currentPage() {
  let arr = window.location.pathname.split('/')
  return arr[arr.length-1]
}

function optionsSelectionInit(list) {
    console.log("optionsSelectionInit");
    const select = document.getElementById('option');
    select.innerHTML = ''
    list.forEach(item => {
        const option = document.createElement('option');
        option.value = item;
        option.text = item;
        select.add(option);
    });
}


function getRandomNumbers(max, num) {
  // 生成0-max之间的数，不包括max
  const numbers = Array.from({ length: max }, (_, i) => i);
  
  // Fisher-Yates洗牌算法
  for (let i = numbers.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [numbers[i], numbers[j]] = [numbers[j], numbers[i]];
  }
  
  // 取前num个元素
  return numbers.slice(0, num);
}

function randdomImgDivs(list, num) {
    let ids = [];
    let max = list.length;
    while (num > max) {
        max += list.length;
    }
    ids = getRandomNumbers(max, num);
    let html = ''
    for (let id of ids) {
        id = id % list.length;
        html += `<div class="col-3">
        <img src="${list[id]}" class="img-fluid" alt="${list[id]}">
    </div>`;
    }
    return html;
}


